function Contact() {
    try {
        const [formData, setFormData] = React.useState({
            name: '',
            email: '',
            subject: '',
            message: ''
        });
        const [isSubmitting, setIsSubmitting] = React.useState(false);
        const [submitStatus, setSubmitStatus] = React.useState(null); // null | 'success' | 'error'

        const handleSubmit = async (e) => {
            e.preventDefault();
            setIsSubmitting(true);
            setSubmitStatus(null);
            try {
                const formData = new FormData(e.target);
                formData.append("access_key", "51725bc0-0023-4d7e-a3ad-111ca4a98cc2");
                const response = await fetch("https://api.web3forms.com/submit", {
                    method: "POST",
                    body: formData
                });
                const data = await response.json();
                if (data.success) {
                    console.log('Form submitted successfully:', data);
                    setFormData({ name: '', email: '', subject: '', message: '' });
                    setSubmitStatus('success');
                } else {
                    setSubmitStatus('error');
                }
            } catch (error) {
                setSubmitStatus('error');
            } finally {
                setIsSubmitting(false);
                setTimeout(() => setSubmitStatus(null), 60000); // Reset after 1 minute
            }
        };

        const handleChange = (e) => {
            setFormData({ ...formData, [e.target.name]: e.target.value });
        };

        return (
            <section id="contact" data-name="contact" data-file="components/Contact.js" className="py-20 bg-black">
                <div className="container mx-auto px-6">
                    <div className="text-center mb-16">
                        <h2 className="text-4xl font-bold text-white mb-4">Get In Touch</h2>
                        <p className="text-xl text-gray-300">Ready to transform your ideas into reality?</p>
                    </div>

                    {/* Contact form full width */}
                    <form onSubmit={handleSubmit} className="bg-gray-900 rounded-2xl p-8 shadow-lg border border-gray-800 max-w-2xl mx-auto">
                        <div className="grid md:grid-cols-2 gap-4 mb-4">
                            <input
                                type="text"
                                name="name"
                                placeholder="Your Name"
                                value={formData.name}
                                onChange={handleChange}
                                required
                                className="w-full px-4 py-3 border border-gray-700 rounded-lg focus:ring-2 focus:ring-white focus:border-white bg-gray-800 text-white placeholder-gray-400"
                            />
                            <input
                                type="email"
                                name="email"
                                placeholder="Your Email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                className="w-full px-4 py-3 border border-gray-700 rounded-lg focus:ring-2 focus:ring-white focus:border-white bg-gray-800 text-white placeholder-gray-400"
                            />
                        </div>
                        <select
                            name="subject"
                            value={formData.subject}
                            onChange={handleChange}
                            required
                            className="w-full px-4 py-3 border border-gray-700 rounded-lg focus:ring-2 focus:ring-white focus:border-white bg-gray-800 text-white mb-4"
                        >
                            <option value="">Select a Service</option>
                            <option value="Enquiry for AR/VR Development">AR/VR Development</option>
                            <option value="Enquiry for AI/ML Solutions">AI/ML Solutions</option>
                            <option value="Enquiry for Web Development">Web Development</option>
                        </select>
                        <textarea
                            name="message"
                            placeholder="Tell us about your project..."
                            value={formData.message}
                            onChange={handleChange}
                            required
                            rows="4"
                            className="w-full px-4 py-3 border border-gray-700 rounded-lg focus:ring-2 focus:ring-white focus:border-white bg-gray-800 text-white placeholder-gray-400 mb-6"
                        ></textarea>
                        <button
                            type="submit"
                            disabled={isSubmitting}
                            className={`w-full py-3 rounded-lg font-semibold transition-colors disabled:opacity-50 
                                ${isSubmitting ? 'bg-white text-black' : submitStatus === 'success' ? 'bg-green-500 text-white' : submitStatus === 'error' ? 'bg-red-500 text-white' : 'bg-white text-black hover:bg-gray-100'}`}
                        >
                            {isSubmitting ? 'Sending...' : submitStatus === 'success' ? 'Yayy! We\'ll respond ASAP ðŸš€ ' : submitStatus === 'error' ? 'Error! Try Again' : 'Send Message'}
                        </button>
                    </form>
                </div>
            </section>
        );
    } catch (error) {
        console.error('Contact component error:', error);
        reportError(error);
        return (
            <section className="py-20 bg-black">
                <div className="container mx-auto px-6 text-center">
                    <p className="text-gray-500">Unable to load contact section</p>
                </div>
            </section>
        );
    }
}
