function Services() {
    try {
        const services = [
            {
                icon: 'fas fa-vr-cardboard',
                title: 'AR/VR Development',
                description: 'Interactive experiences for your visitors to increase engagement and conversion rates',
                features: ['WebAR Applications', 'WebVR Solutions', 'Mixed Reality Experiences'],
            },
            {
                icon: 'fas fa-brain',
                title: 'AI/ML Solutions',
                description: 'Intelligent systems that learn, adapt, and improve over time to provide better user experiences',
                features: ['Computer Vision', 'Machine Learning Models', 'Natural Language Processing'],
            },
            {
                icon: 'fas fa-code',
                title: 'Web Development',
                description: 'Websites and WebApps that look modern and work smoothly on any device, from phones to big screens',
                features: ['Responsive Design', 'Progressive Web Apps', 'Cloud Integration'],
            }
        ];

        const scrollToSection = (sectionId) => {
            const element = document.getElementById(sectionId);
            if (element) {
                element.scrollIntoView({ behavior: 'smooth' });
            }
        };

        return (
            <section id="services" data-name="services" data-file="components/Services.min.js" className="py-20 bg-black">
                <div className="container mx-auto px-6">
                    <div className="text-center mb-16">
                        <h2 className="text-4xl font-bold text-white mb-4">Our Services</h2>
                        <p className="text-xl text-gray-300 max-w-3xl mx-auto">
                            We deliver comprehensive technology solutions that transform ideas into reality
                        </p>
                    </div>

                    <div className="grid md:grid-cols-3 gap-8">
                        {services.map((service, index) => (
                            <div key={index} className="service-card bg-gray-900 rounded-2xl p-8 shadow-lg border border-gray-700 flex flex-col items-center">
                                <div className="w-16 h-16 bg-white rounded-2xl flex items-center justify-center mb-6">
                                    <i className={`${service.icon} text-2xl text-black`}></i>
                                </div>
                                <h3 className="text-2xl font-bold text-white mb-4 text-center">{service.title}</h3>
                                <p className="text-gray-300 mb-6 text-center">{service.description}</p>
                                <ul className="space-y-2 w-full mb-6">
                                    {service.features.map((feature, idx) => (
                                        <li key={idx} className="flex items-center text-gray-400">
                                            <i className="fas fa-check text-white mr-3"></i>
                                            {feature}
                                        </li>
                                    ))}
                                </ul>
                                <button 
                                    onClick={() => {
                                        scrollToSection('home');
                                        const routes = ['/services/ar-vr', '/services/ai-ml', '/services/web-dev'];
                                        Router.navigate(routes[index]);
                                    }}
                                    className="w-full bg-white text-black py-3 rounded-lg font-semibold hover:bg-gray-100 transition-colors"
                                >
                                    Click to Learn More
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        );
    } catch (error) {
        console.error('Services component error:', error);
        reportError(error);
    }
}
