const Router = {
    currentRoute: '/',
    
    navigate: (route) => {
        Router.currentRoute = route;
        window.history.pushState({}, '', route);
        window.dispatchEvent(new Event('routechange'));
    },
    
    back: () => {
        window.history.back();
    },
    
    init: () => {
        window.addEventListener('popstate', () => {
            Router.currentRoute = window.location.pathname;
            window.dispatchEvent(new Event('routechange'));
        });
        
        Router.currentRoute = window.location.pathname;
    }
};